import Marpit from '@marp-team/marpit'
import { Upload, message, Button, Icon } from 'antd';
import React from "react";
var fs = require("fs");
// Upload
const props = {
    name: 'file',
    action: 'https://www.mocky.io/v2/5cc8019d300000980a055e76',
    headers: {
        authorization: 'authorization-text',
    },
};

// 1. Create instance (with options if you want)
const marpit = new Marpit()

// 2. Add theme CSS
const theme = `
/* @theme example */

section {
  background-color: #369;
  color: #fff;
  font-size: 30px;
  padding: 40px;
}

h1,
h2 {
  text-align: center;
  margin: 0;
}

h1 {
  color: #8cf;
}
`
marpit.themeSet.default = marpit.themeSet.add(theme)



class MyUpload extends React.Component{
    constructor(props) {
        super(props);
        this.beforeUpload.bind = this.beforeUpload.bind(this);
        this.result = new String;
    }
    state = {
        file: "",
        html: ""
    }

    beforeUpload = (file) => {
        this.setState({
            file:file
        });
    }
    onChange = (info) => {
        if (info.file.status !== 'uploading') {
            console.log(info.file, info.fileList);
        }
        if (info.file.status === 'done') {
            message.success(`${info.file.name} file uploaded successfully`);
        } else if (info.file.status === 'error') {
            message.error(`${info.file.name} file upload failed.`);
        }
    }

    onPreview = (file) => {
        var reader = new FileReader();
        reader.onload = function (event) {
            var contents = event.target.result;
            console.log("File contents: " + contents);
        };

        reader.onerror = function (event) {
            console.error("File could not be read! Code " + event.target.error.code);
        };
        reader.readAsText(file, "utf-8");
        this.result = reader.result;
        // this.setState({
        //     html:result
        // });
        // const { html, css } = marpit.render(this.result);
        const { html, css } = marpit.render(this.result);

// 4. Use output in your HTML
        this.result = `
            <!DOCTYPE html>
            <html><body>
              <style>${css}</style>
              ${html}
            </body></html>
            `

        return (
            <div>
                {this.result}
            </div>
        )
    }

    render() {

        // const { html, css } = marpit.render(this.state.html);
        //
        // // 4. Use output in your HTML
        // const converthtml = `
        //     <!DOCTYPE html>
        //     <html><body>
        //       <style>${css}</style>
        //       ${html}
        //     </body></html>
        //     `

        const converthtml = this.result

        return(
            <div>
                <Upload
                    onChange={this.onChange}
                    beforeUpload={this.beforeUpload}
                    onPreview={this.onPreview}
                    {...props}>

                    <Button>
                        <Icon type="upload" /> Click to Upload
                    </Button>

                </Upload>


                <div>
                {converthtml}
                </div>
            </div>

        )
    }
}

export default MyUpload;
